var fs = require("fs");

var data = fs.readFileSync('asd.txt'); //blocking call

console.log(data.toString()); 


//below is non-blocking code -- this call procedes and executes the rest of the code as it's waiting for the file to load?
fs.readFile('asd.txt', function (err, data) {
	if (err) return console.error(err);
	console.log(data.toString());
});

console.log("Program Ended");